mapper.select:
select * from ( select a.id, a.name, u.name as owner, a.startdate, a.enddate, rownum rw
from tbl_Activity a join tbl_user u on a.owner=u.id
a.name like '%'||#{name}||'%'
and u.name like '%'||#{owner}||'%'
and a.startdate > #{startdate}
and a.enddate < #{enddate}
order by a.createtime desc ) rw > #{skipCount} and rw < #{pageSize}

select count(*) from( select * from ( select a.id, a.name, u.name as owner, a.startdate, a.enddate, rownum rw
from tbl_Activity a join tbl_user u on a.owner=u.id
a.name like '%'||#{name}||'%'
and u.name like '%'||#{owner}||'%'
and a.startdate > #{startdate}
and a.enddate < #{enddate}
order by a.createtime desc)
rw > #{skipCount} and rw < #{pageSize}
)
